import React from 'react';
import './App.css';
import IsRaining from './isRaining';

class App extends React.Component {

  constructor() {
    super();
    this.state = {
      isRain : true
    }
  }

  checkRain(){
    fetch('https://api.openweathermap.org/data/2.5/weather?q=London,uk&APPID=0b01af6613245e221ab06100bd31ac4d')
  }

  render () {
    return (
      <IsRaining 
      isRain={this.state.isRain}
      />
    );
  }

}

export default App;
